'use strict';

/* eslint-env mocha */

var chai = require('chai');
var chaiAsPromised = require('chai-as-promised');
chai.use(chaiAsPromised);
var expect = chai.expect;

var _require = require('./testHelpers'),
    client = _require.client;

describe('Balance', function () {

  describe('list by specific account', function () {
    it('simple example', function () {
      return client.balances.list({
        account_alias: 'default'
      }).then(function (items) {
        return expect(items[0].amount).not.to.be.empty;
      });
    });
  });

  describe('listAll', function () {
    it('simple example', function () {
      return client.balances.listAll().then(function (resp) {
        expect(resp.find(function (b) {
          return b.account_alias == 'default';
        }).amount).not.to.be.empty;
      });
    });
  });
});