'use strict';

/* eslint-env mocha */

var chai = require('chai');
var chaiAsPromised = require('chai-as-promised');
chai.use(chaiAsPromised);
var expect = chai.expect;

var _require = require('./testHelpers'),
    client = _require.client;

describe('Core Status', function () {

  describe('gasRate', function () {
    it('simple example', function () {
      return client.status.gasRate().then(function (items) {
        return expect(items.gas_rate).not.to.be.empty;
      });
    });
  });

  describe('netInfo', function () {
    it('simple example', function () {
      return client.status.netInfo().then(function (resp) {
        expect(resp).not.to.be.empty;
      });
    });
  });
});